import React from 'react';
import './App.css';
import {useState} from 'react';

function App() {

  const [toDoName , setToDoName] = useState('');
  const [toDoList , setToDoList] = useState([]);
  const [toDoIndex , setToDoIndex] = useState(1);

  const addToDoList = () => {
    let toDoItemList = toDoList;
    toDoItemList.push({'name': toDoName, 'key' : toDoIndex});

    setToDoList(toDoItemList);
    setToDoName('');
    setToDoIndex(toDoIndex + 1)
  }

  const removeToDoItem = (key) => {
    let ToDoItemList = toDoList.filter(todo => todo.key !== key )
    setToDoList(ToDoItemList)

  }

  return (
    <div>
      <h1 className='titleToDoList'>Voici votre liste des choses Ã  faire !!</h1>
      <div className="app">
        <input className="myInput" type="text" onChange= {(e) => setToDoName(e.target.value)}/>
        <button onClick={addToDoList}>Cliquer ici</button>
      <ul>
      {toDoList && toDoList.length === 0 ? <p>Il n'y a rien au programme pour le moment</p> : ""}
        {toDoList && toDoList.length > 0 && toDoList.map( todo => {
          return (

          <div className='todoList' key={todo.key}>
            <li key={todo.key}>{todo.name}</li>
            <li key={todo.key+1}><button onClick={() => removeToDoItem(todo.key)}>X</button></li>   
          </div> 
        ) }  ) } 

      </ul>

      </div>
    </div>
  );
}

export default App;
