import React from 'react';
import './App.css';
import {useState} from 'react';

function App() {

  const [toDoName , setToDoName] = useState('');
  const [toDoList , setToDoList] = useState([]);
  const [toDoIndex , setToDoIndex] = useState(1);

  const addToDoList = () => {
    let toDoItemList = toDoList;
    toDoItemList.push({'name': toDoName, 'index' : toDoIndex});

    setToDoList(toDoItemList);
    setToDoName('');
    setToDoIndex(toDoIndex + 1)
  }

  const removeToDoItem = (key) => {
    let ToDoItemList = toDoList.filter(todo => todo.key !== key )
    setToDoList(ToDoItemList)

  }

  return (
    <div className="app">
      <input className="myInput" type="text" onChange= {(e) => setToDoName(e.target.value)}/>
      <button onClick={addToDoList}>Cliquer ici</button>
    <ul>
      {toDoList && toDoList.length > 0 && toDoList.map( todo => {
        return (

        <div>
          <li key={todo.index}>{todo.name}</li>
          <li><input onClick={() => removeToDoItem(todo.index)}>X</input></li>
        </div>
      )} ) }
    </ul>

    </div>
  );
}

export default App;
